function WIfunc_plotconfustionmats()

global handles

if isfield(handles.decode,'confmats')
    try
        delete(handles.decode.confmats([3 7]))
    end
end

handles.decode.confmats(1) = ...
    subplot('Position',[.45 .175 .2 .2]);
handles.decode.confmats(2) = ...
    imagesc(handles.decode.SPKCNTconfusionmatrix);
axis square
title('Spike Count','fontsize',16)
set(gca,'xtick',1:handles.nclasses,'ytick',1:handles.nclasses)
set(gca,'fontsize',14)
xlabel('Actual Stimulus','fontsize',15)
ylabel('Predicted Stimulus','fontsize',15)
handles.decode.confmats(3) = ...
    uicontrol('Style','text','Units','normalized',...
    'Position',...
    [0.45 .025 .2 .08],...
    'String',['Decoding performance ' ...
    num2str(handles.decode.SPKCNTperf)],...
    'BackgroundColor', [1 1 1]*.99,...
    'ForegroundColor', [1 1 1]*0,...
    'horizontalAlignment', 'center',...
    'fontsize',14);

% handles.decode.confmats(4) = ...
%     colorbar; caxis([0 1])
% aux = get(handles.decode.confmats(4),'Position');
% set(handles.decode.confmats(4),'Position',...
%     [0.65 .375-aux(4)*.5 aux(3)*.5 aux(4)*.5]);

handles.decode.confmats(5) = ...
    subplot('Position',[.7375 .175 .2 .2]);
handles.decode.confmats(6) = ...
    imagesc(handles.decode.WVconfusionmatrix);
axis square
title('Wavelets','fontsize',16)
set(gca,'xtick',1:handles.nclasses,'ytick',1:handles.nclasses)
set(gca,'fontsize',14)
xlabel('Actual Stimulus','fontsize',15)
ylabel('Predicted Stimulus','fontsize',15)
handles.decode.confmats(7) = ...
    uicontrol('Style','text','Units','normalized',...
    'Position',...
    [0.7375 .025 .2 .08],...
    'String',['Decoding performance ' ...
    num2str(handles.decode.WVperf)],...
    'BackgroundColor', [1 1 1]*.99,...
    'ForegroundColor', [1 1 1]*0,...
    'horizontalAlignment', 'center',...
    'fontsize',14);
% handles.decode.confmats(8) = ...
%     colorbar; caxis([0 1])
% aux = get(handles.decode.confmats(8),'Position');
% set(handles.decode.confmats(8),'Position',...
%     [.9375 .375-aux(4)*.5 aux(3)*.5 aux(4)*.5]);

end

